import React from 'react';

export default class Profile extends React.Component{

  state = {profile:{}}

  componentDidMount(){
    const data = {name:'Name',rollNo:'17BECXXX',batch:'2017-2021',mentor:'MentorName',attendance:100,academicDetails:'None',imgUrl:'data:image/jpeg;base64,'}
    this.setState({profile:data})
  }

  render(){

    return(
  <div style={profileContainerStyle}>
    <div style={{marginBottom:'100px'}}>
    <center>
      <h1>My Profile</h1>
    </center>
    </div>
    <div style={{justifyContent:"space-around",flexDirection:"row",display:'flex',border:'1px solid gray',padding:'20px',margin:'10px',borderRadius:'5px'}}>
    <div>
    <img src={this.state.profile.imgUrl} alt="No image" style={profileImageStyle}/>
    </div>
    <div>
    <p><b>Name:</b>{this.state.profile.name}</p>
    <p><b>RollNo:</b>{this.state.profile.rollNo}</p>
    <p><b>Batch:</b>{this.state.profile.batch}</p>
    <p><b>Mentor Name:</b>{this.state.profile.mentor}</p>
    <p><b>Attendance:</b>{this.state.profile.attendance}</p>
    <p><b>Academic Details:</b>{this.state.profile.academicDetails}</p>
    </div>
    </div>
  </div>
);
}
}

const profileContainerStyle = {

    fontFamily:"Helvetica",
    fontSize: '20px',
}

const profileImageStyle = {
  height:"250px",
  width:"250px",
  borderRadius:"50%"
}
