import React,{useState} from 'react';
import RenderStudentLogin from './Pages/StudentPages/RenderStudentLogin';
import RenderMentorLogin from './Pages/MentorPages/RenderMentorLogin';




function App(){
  const [pageSetter,setPageSetter] = useState('student')
  const handlePage = (e) => {
    setPageSetter(e.target.name);
  }
  if(pageSetter==='student'){
return(<div>
  <RenderStudentLogin/>
  <p onClick={handlePage()} name='mentor'>Mentor Login</p>
  </div>
)}
else{
  return(<div>
    <RenderMentorLogin/>
    <p onClick={handlePage()} name='student'>Student Login</p>
    </div>)
}
}
export default App;
